/* Custom styles for family tree */
.family-tree-container {
  @apply relative;
}

.family-node {
  @apply transition-transform duration-200 ease-in-out;
}

.family-node:hover {
  @apply shadow-lg transform scale-105;
}

.tree-level {
  @apply relative;
}

.tree-branch {
  @apply relative;
}

.tree-node-container {
  @apply relative;
}

/* Connection lines */
.tree-connection-line {
  @apply absolute bg-muted-foreground/30;
}

/* Animations */
@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(-10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

.animate-fade-in {
  animation: fadeIn 0.3s ease-out;
}

/* Scrollbar styling */
.family-tree::-webkit-scrollbar {
  width: 8px;
  height: 8px;
}

.family-tree::-webkit-scrollbar-track {
  @apply bg-muted rounded-lg;
}

.family-tree::-webkit-scrollbar-thumb {
  @apply bg-muted-foreground/50 rounded-lg;
}

.family-tree::-webkit-scrollbar-thumb:hover {
  @apply bg-muted-foreground/70;
}

/* Text truncation utilities */
.line-clamp-3 {
  display: -webkit-box;
  -webkit-line-clamp: 3;
  -webkit-box-orient: vertical;
  overflow: hidden;
}
